import { useEffect } from "react"
import {
    QueryClient,
    QueryClientProvider,
    useQuery,
  } from "@tanstack/react-query";


async function getTypyLinek (IP) {
    let response = await fetch(`http://${IP}:3005/getTypyLinek`);
    return response.json();
}
async function getOperations(IP, linkaId){
    let response = await fetch(`http://${IP}:3005/getOperationsByLineId`, {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
        },
        body: JSON.stringify({
            PLine_id: linkaId,
        }),   
    })
    return response.json();
}
async function getProcesses(IP, linkaId){
    let response = await fetch(`http://${IP}:3005/getProcessesByLineId`, {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
        },
        body: JSON.stringify({
            PLine_id: linkaId,
        }),   
    })
    return response.json();
}



function AdminConfig() {
    const {isLoading, operations} = useQuery({queryKey: ['typyLinek'], queryFn: async () => await getOperations(IP, ), initialData: []});
    const {isLoadingX, processes} = useQuery({queryKey: ['typyLinek'], queryFn: async () => await getProcesses(IP, ), initialData: []});  
    
    
    if (isLoading) {
        return <span>Loading...</span>
        }
    if (isLoadingX) {
        return <span>Loading...</span>
        }

    return (        
        <div className="bg-slate-300 w-11/12 h-full flex flex-col">
            <div>
                <h1 className="text-3xl pt-10 pb-10 text-center">{linkaName}</h1>
            </div>
            {operations.map((operation) => {
                return (
                    <div className="bg-white flex flex-col border">
                        <div className="flex frex-row mb-5 mt-5 items-center">
                            <div className="bg-white">
                                <p className="bg-slate-200 text-xl p-2 ml-4">{operation.Operation_name}</p>
                                </div>
                        </div>
                    </div>
                );
                })}
            <div className="pt-5 pl-5 pb-5 border">
                <button className="ml-5 rounded-full bg-slate-200 hover:bg-slate-400 p-2" onClick={() => createNewOperation(linkaId)}>+Add new Operation</button>
            </div>
        </div>
    );
}
export default AdminConfig;
